import React, { Component } from 'react'

 class App extends Component {
   // i need a state to handle the local data in the component

     constructor(props){
      super(props);
      this.state = {
        username : "",
        password : ""
      }
     }

     onLogin =() =>{
        const {username,password}= this.state;
        alert(`username :${username}password:${password}`)
     }

     onReset = () =>{
        this.setState (
            {
                username :"",
                password :""
            }
        )
     }

     onChange = (event,field) =>{
        this.setState (
            {
              [field] : event.target.value
            }
        )
     }

  render() {

    const {username,password} = this.state;

    return (
      <div style={{ border:'1px solid green', paddingTop:'30px',margin :'10px'}}>
         <center><h1 style={{color:'blue'}}> Form Handing</h1></center>
        <hr/>
        <br/>

        <center>
        <label> Login Form</label>
        <br/> <br/>
        <label> Username:</label>
        <input type = "username" value={username} onChange = {(event) => {this.onChange(event,'username')}}/>
        <br/> <br/>
        <label> Password:</label>
        <input type="password" value={ password} onChange ={(event) =>{this.onChange(event,'password')}}/>
        <br/> <br/>
        <button onClick={ this.onLogin}> Login</button>
        <button onClick={ this.onReset}> Reset</button>
        </center>
      </div>
    )
  }
}
export default App;
